import React, { useState } from 'react';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isPaused, setIsPaused] = useState(false);

  const sendMessage = async () => {
    if (!input) return;
    
    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput("");

    const response = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ session_id: "test-session", message: input })
    });
    
    const data = await response.json();
    
    // Check if we hit the Day 3 "Summarize & Confirm" Checkpoint
    if (data.reply.includes("Section A: My Understanding")) {
      setIsPaused(true);
    }

    setMessages([...newMessages, { role: 'bot', content: data.reply }]);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h2>Dr. Malpani's AI Consultation</h2>
      <div style={{ height: '400px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px' }}>
        {messages.map((m, i) => (
          <div key={i} style={{ textAlign: m.role === 'user' ? 'right' : 'left' }}>
            <p><strong>{m.role === 'user' ? 'You' : 'Doctor AI'}:</strong></p>
            <pre style={{ whiteSpace: 'pre-wrap', fontFamily: 'inherit' }}>{m.content}</pre>
          </div>
        ))}
      </div>
      
      <div style={{ marginTop: '10px' }}>
        <input 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder={isPaused ? "Confirm the summary above..." : "Type your history..."}
          style={{ width: '80%' }}
        />
        <button onClick={sendMessage}>{isPaused ? "Confirm" : "Send"}</button>
      </div>
    </div>
  );
}

export default App;
